// VoiceRecognition.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const VoiceRecognition = () => {
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [messages, setMessages] = useState([]);
  const [recognizedGesture, setRecognizedGesture] = useState('');
  const [gestureImage, setGestureImage] = useState('');

  const handleListen = () => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Your browser does not support speech recognition. Please try Chrome.');
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onresult = (event) => {
      let finalTranscript = '';
      for (let i = event.resultIndex; i < event.results.length; ++i) {
        if (event.results[i].isFinal) {
          finalTranscript += event.results[i][0].transcript;
        }
      }
      setTranscript(finalTranscript);
    };

    if (isListening) {
      recognition.stop();
    } else {
      recognition.start();
    }

    recognition.onend = () => {
      if (transcript !== '') {
        setMessages((prevMessages) => [...prevMessages, transcript]);
        sendTextToBackend(transcript);
      }
      setIsListening(false);
    };
  };

  useEffect(() => {
    if (transcript !== '') {
      setMessages((prevMessages) => [...prevMessages, transcript]);
      sendTextToBackend(transcript);
      setTranscript('');
    }
  }, [transcript]);

  const sendTextToBackend = async (text) => {
    try {
      const response = await axios.post('http://localhost:5000/api/voice_recognition', { text });
      setRecognizedGesture(response.data.gesture);
      fetchGestureImage(response.data.gesture);
    } catch (error) {
      console.error('Error sending text to backend:', error);
    }
  };

  const fetchGestureImage = async (gesture) => {
    try {
      const response = await axios.get(`http://localhost:5000/gesture_images/${gesture}`);
      const images = response.data.images;
      const randomImage = images[Math.floor(Math.random() * images.length)];
      setGestureImage(`http://localhost:5000/gesture_images/${gesture}/${randomImage}`);
    } catch (error) {
      console.error('Error fetching gesture image:', error);
    }
  };

  return (
    <div className="flex flex-col items-center mt-12">
      <h1 className="text-3xl font-bold mb-4">Real-Time Voice Recognition</h1>
      <button
        onClick={handleListen}
        className={`py-2 px-4 rounded-lg mt-4 ${isListening ? 'bg-red-500' : 'bg-blue-500'} text-white`}
      >
        {isListening ? 'Stop Listening' : 'Start Listening'}
      </button>
      <div className="mt-4 w-2/3 p-4 bg-gray-100 rounded-lg max-h-screen overflow-y-auto">
        {messages.map((message, index) => (
          <div key={index} className="bg-green-500 text-white py-2 px-4 rounded-lg mb-2 text-right">
            {message}
          </div>
        ))}
      </div>
      {gestureImage && (
        <div className="mt-4 p-4 bg-yellow-100 rounded-lg">
          <h2 className="text-xl font-bold mb-2">Recognized Gesture</h2>
          <img src={gestureImage} alt="Recognized Gesture" />
        </div>
      )}
    </div>
  );
};

export default VoiceRecognition;
