import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const VideoRecognition = () => {
  const [gesture, setGesture] = useState(null);
  const [error, setError] = useState(null);
  const videoRef = useRef(null);

  useEffect(() => {
    const startVideo = async () => {
      try {
        const constraints = {
          video: {
            width: { ideal: 1280 },
            height: { ideal: 720 },
            facingMode: 'user'
          }
        };
        const stream = await navigator.mediaDevices.getUserMedia(constraints);
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        setError('Could not start video source');
        console.error('Error accessing webcam: ', err);
      }
    };
    startVideo();
  }, []);

  const recognizeGestureFromVideo = async () => {
    if (!videoRef.current) return;

    const video = videoRef.current;
    const canvas = document.createElement('canvas');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL('image/jpeg');

    try {
      const response = await axios.post('/api/recognize_gesture', { image: dataUrl });
      setGesture(response.data.gesture);
    } catch (error) {
      console.error('Error recognizing gesture:', error);
    }
  };

  return (
    <div className="flex h-screen">
      <div className="w-1/2 flex justify-center items-center bg-gray-200">
        {error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <video ref={videoRef} className="w-full h-auto border border-gray-300" autoPlay playsInline></video>
        )}
      </div>
      <div className="w-1/2 flex flex-col justify-center items-center bg-gray-100">
        <h1 className="text-3xl font-bold mb-4">Real-Time Video Gesture Recognition</h1>
        <button 
          onClick={recognizeGestureFromVideo} 
          className="bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-700 mt-4"
        >
          Recognize Gesture
        </button>
        {gesture ? (
          <p className="mt-6 text-lg text-gray-600">Recognized Gesture: {gesture}</p>
        ) : (
          <p className="mt-6 text-lg text-gray-600">Gesture will appear here</p>
        )}
      </div>
    </div>
  );
};

export default VideoRecognition;
