import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

const VideoRecognition = () => {
  const [gesture, setGesture] = useState(null);
  const videoRef = useRef(null);

  useEffect(() => {
    // Access the user's webcam
    const startVideo = async () => {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    };
    startVideo();

    // Capture frames and send to backend via WebSocket
    const interval = setInterval(sendFrame, 1000); // Adjust the interval as needed
    return () => clearInterval(interval); // Clean up on component unmount
  }, []);

  const sendFrame = async () => {
    const video = videoRef.current;
    if (!video) return;

    const canvas = document.createElement('canvas');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL('image/jpeg');

    socket.emit('frame', dataUrl);
  };

  useEffect(() => {
    socket.on('prediction', data => {
      if (data.gesture) {
        setGesture(data.gesture);
      } else if (data.error) {
        console.error('Error:', data.error);
      }
    });
  }, []);

  return (
    <div className="text-center mt-12">
      <h1 className="text-3xl font-bold mb-4">Real-Time Video Gesture Recognition</h1>
      <video ref={videoRef} className="w-96 h-72 border border-gray-300 mt-4" autoPlay playsInline></video>
      {gesture ? (
        <p className="mt-6 text-lg text-gray-600">Recognized Gesture: {gesture}</p>
      ) : (
        <p className="mt-6 text-lg text-gray-600">Gesture will appear here</p>
      )}
    </div>
  );
};

export default VideoRecognition;
