import React, { useState } from 'react';
import axios from 'axios';

const VoiceRecognition = () => {
  const [inputText, setInputText] = useState('');
  const [gestureImage, setGestureImage] = useState('');

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/text_to_image', { text: inputText });
      const gesture = response.data.gesture;
      setGestureImage(`http://localhost:5000/gesture_images/${gesture}`);
    } catch (error) {
      console.error('Error fetching gesture image:', error);
      alert('No gesture found for the provided text.');
    }
  };

  return (
    <div className="flex flex-col items-center mt-12">
      <h1 className="text-3xl font-bold mb-4">Text to Image Recognition</h1>
      <form onSubmit={handleSubmit} className="mb-4">
        <input
          type="text"
          value={inputText}
          onChange={handleInputChange}
          placeholder="Enter text"
          className="border rounded-lg py-2 px-4 mr-2"
        />
        <button type="submit" className="py-2 px-4 rounded-lg bg-blue-500 text-white">
          Submit
        </button>
      </form>
      {gestureImage && (
        <div className="mt-4 p-4 bg-yellow-100 rounded-lg">
          <h2 className="text-xl font-bold mb-2">Recognized Gesture</h2>
          <img src={gestureImage} alt="Recognized Gesture" />
        </div>
      )}
    </div>
  );
};

export default VoiceRecognition;
