import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const VideoRecognition = () => {
  const [gesture, setGesture] = useState(null);
  const videoRef = useRef(null);

  useEffect(() => {
    // Access the user's webcam
    const startVideo = async () => {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    };
    startVideo();
  }, []);

  const recognizeGestureFromVideo = async () => {
    const video = videoRef.current;
    const canvas = document.createElement('canvas');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL('image/jpeg');

    try {
      const response = await axios.post('http://localhost:5000/api/recognize_gesture', { image: dataUrl });
      setGesture(response.data.gesture);
    } catch (error) {
      console.error('Error recognizing gesture:', error);
    }
  };

  return (
    <div className="text-center mt-12">
      <h1 className="text-3xl font-bold mb-4">Real-Time Video Gesture Recognition</h1>
      <video ref={videoRef} className="w-96 h-72 border border-gray-300 mt-4" autoPlay playsInline></video>
      <button 
        onClick={recognizeGestureFromVideo} 
        className="bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-700 mt-4"
      >
        Recognize Gesture
      </button>
      {gesture ? (
        <p className="mt-6 text-lg text-gray-600">Recognized Gesture: {gesture}</p>
      ) : (
        <p className="mt-6 text-lg text-gray-600">Gesture will appear here</p>
      )}
    </div>
  );
};

export default VideoRecognition;
